import React, { Component } from "react";
import ReactDOM from 'react-dom';

import analyze from 'rgbaster';

//Hardcoding to get images for now
import theme from './theme.png';

import './index.css';

class Theme extends Component {
    constructor(props){
        super(props);
        this.dominentColor = "red";
        console.log(this.dominentColor);
    }
    //Get the image from Firebase, might need to bind in constructor to pass it in a function

    dominentColor = () =>{
        this.dominentColor = analyze(theme);
        return this.dominentColor[0].color;
    }

    render(){
        return(
            <div className="ThemeContainer" style={{backgroundColor:this.dominentColor}}>
                <img className="ThemeImage" src = {theme}></img>
            </div>
        )
    }
}

class MainComponenet extends Component {
    render(){
        return(
            <Theme />
        )
    }
}

ReactDOM.render(<MainComponenet/>,document.getElementById("root"))
