import React, { Component } from "react";
import ReactDOM from 'react-dom';

import Palette from 'react-palette'

//Hardcoding to get images for now
import theme from './theme.png';

import './index.css';

class Theme extends Component {
    constructor(props){
        super(props);
        this.state = {
            dominentColor: "red"
        }
        console.log(this.state.dominentColor);
    }
    //Get the image from Firebase, might need to bind in constructor to pass it in a function

    GetdominentColor = () =>{
        const color = analyze(theme);
        console.log(color);
    }

    render(){
        return(
            <div className="ThemeContainer" style={{backgroundColor:this.state.dominentColor}}>
                <img className="ThemeImage" src = {theme} alt="Theme"></img>
                <button onClick = {this.GetdominentColor}></button>
            </div>

        )
    }
}

class MainComponenet extends Component {
    render(){
        return(
            <Theme />
        )
    }
}

ReactDOM.render(<MainComponenet/>,document.getElementById("root"))
