import React, { Component } from "react";
import ReactDOM from 'react-dom';

import Palette from 'react-palette'

//Hardcoding to get images for now
import theme from './theme.png';

import './index.css';

class Theme extends Component {
    //Get the image from Firebase, might need to bind in constructor to pass it in a function
    render(){
        return(
            <Palette src={theme}>
                {
                    ({data,loading,error}) => (
                        <div style={{backgroundColor:data.vibrant}}></div>
                    )
                }
            </Palette>        
        )
    }
}

class MainComponenet extends Component {
    render(){
        return(
            <Theme />
        )
    }
}

ReactDOM.render(<MainComponenet/>,document.getElementById("root"))
